pragma solidity ^0.8.0;

/// @title Purpose-Bound Money (PBM) Interface
/// @notice The PBM interface facilitates logical constraints on the use of ERC-20 tokens by acting as a wrapper around them.
interface IPBM {
    struct UserBalance {
        uint256 walletBalance;
        uint256 availableBalance;
    }

    /// @notice Initializes the contract with the address of the underlying ERC20 token, contract expiration, and the PBM address list.
    /// @param _spotToken The address of the underlying ERC20 token.
    /// @param _expiry Global expiry timestamp for the contract.
    function initialise(address _spotToken, uint256 _expiry) external;

    /// @notice Creates a new PBM token type using the provided data.
    /// @param spotAmount Quantity of the underlying ERC-20 token that the PBM represents.
    /// @param tokenExpiry Expiration timestamp specific to this PBM token type.
    /// @param tokenURI URI pointing to metadata compliant with the Opensea NFT metadata standard.
    /// @param postExpiryURI URI pointing to metadata for the expired PBM type, following the Opensea NFT metadata standard.
    function createPBMTokenType(
        uint256 spotAmount,
        uint256 tokenExpiry,
        string memory tokenURI,
        string memory postExpiryURI
    ) external;

    /// @notice Mints a new PBM instance, ensuring it's backed by the necessary ERC-20 token value.
    /// @param tokenId Identifier of the PBM token type.
    /// @param amount Quantity of PBMs to be minted (should be 1 by design).
    /// @param receiver Address to receive the minted PBMs.
    function mint(uint256 tokenId, uint256 amount, address receiver) external;

    /// @notice Mints multiple PBM instances in a batch for multiple receivers.
    /// @param tokenId Identifier of the PBM token type.
    /// @param amount Quantity of PBMs to be minted for each receiver (should be 1 by design).
    /// @param receivers List of addresses to receive the minted PBMs.
    function mintBatch(uint256 tokenId, uint256 amount, address[] memory receivers) external;

    /// @notice Transfers a PBM without moving the underlying ERC20 tokens.
    /// @param from Address sending the PBM.
    /// @param to Address receiving the PBM.
    /// @param id Identifier of the PBM token type being transferred.
    /// @param amount Quantity of PBM to transfer (should always be 1).
    /// @param data Optional additional data to accompany the transfer.
    function safeTransferFrom(address from, address to, uint256 id, uint256 amount, bytes memory data) external;

    /// @notice Batch transfers multiple PBMs without moving the underlying ERC20 tokens.
    /// @param from Address sending the PBMs.
    /// @param to Address receiving the PBMs.
    /// @param ids List of identifiers for the PBM token types being transferred.
    /// @param amounts List of quantities of PBMs to transfer (each should be 1).
    /// @param data Optional additional data to accompany the transfer.
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) external;

    /// @notice Initiates a new order, locking the specified ERC-20 token value within this contract.
    /// @param customerWalletAddr Address of the customer placing the order.
    /// @param tokenId Identifier of the PBM token type.
    /// @param orderId Platform-generated identifier for the order.
    /// @param orderValue Value of the order in underlying ERC-20 tokens.
    /// @param fundDisbursementAddr Merchant address to receive the ERC-20 tokens upon order redemption.
    function createOrder(
        address customerWalletAddr,
        uint256 tokenId,
        string memory orderId,
        uint256 orderValue,
        address fundDisbursementAddr
    ) external;

    /// @notice Cancels a pending order, refunding the ERC-20 token value to the customer.
    /// @param orderId Platform-generated identifier for the order.
    /// @param tokenId Identifier of the PBM token type related to the order.
    function cancelOrder(string memory orderId, uint256 tokenId) external;

    /// @notice Redeems a pending order, releasing the ERC-20 token value to the specified merchant address.
    /// @param orderId Platform-generated identifier for the order.
    /// @param tokenId Identifier of the PBM token type related to the order.
    /// @param userWallet Address of the customer redeeming the order.
    function redeemOrder(string memory orderId, uint256 tokenId, address userWallet) external;

    /// @notice Fetches the balance details for a user for a specific PBM token.
    /// @param user The address of the user whose balance is to be queried.
    /// @param tokenId The identifier of the PBM token type.
    /// @return userBalance The total wrapped erc20 token balance of the user for the specified PBM token.(walletBalance, availableBalance)
    function getUserBalance(address user, uint256 tokenId) external view returns (UserBalance memory userBalance);

    /// @notice Allows the PBM contract owner to claim the underlying ERC-20 tokens after they expire.
    function revokePBM() external;

    /// @notice Retrieves details of a specific PBM token type.
    /// @param tokenId Identifier of the PBM token type.
    /// @return Amount of underlying ERC-20 tokens represented by the PBM.
    /// @return Expiration timestamp specific to this PBM token type.
    function getTokenDetails(uint256 tokenId) external view returns (uint256, uint256);

    /// @notice Retrieves the URI associated with a specific PBM token type.
    /// @param tokenId Identifier of the PBM token type.
    /// @return URI pointing to metadata compliant with the Opensea NFT metadata standard.
    function uri(uint256 tokenId) external view returns (string memory);

    /// @notice Emitted when a PBM's creator retrieves the ERC-20 tokens from expired PBMs.
    /// @param beneficiary Address of the PBM creator receiving the ERC-20 tokens.
    /// @param ERC20Token Address of the underlying ERC-20 token.
    /// @param ERC20TokenValue Quantity of underlying ERC-20 tokens being transferred.
    event PBMrevokeWithdraw(address beneficiary, address ERC20Token, uint256 ERC20TokenValue);
}
","keccak256":"0x6666796fc54eaba17f90845cd4b9136cb255d71afcde9f100f9dfbd759f37284","license":"GPL-2.0-or-later"},"contracts/IPBMTokenManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/// @title PBMTokenManager Interface.
/// @notice The PBMTokenManager is the stores details of the different data types.
interface IPBMTokenManager {
    /// @notice Returns the URI (metadata) for the PBM with the corresponding tokenId
    /// @param tokenId The id for the PBM in query
    /// @return Returns the metadata URI for the PBM
    function uri(uint256 tokenId) external view returns (string memory);

    /// @notice Checks if tokens Ids have been created and are not expired
    /// @param tokenIds The ids for the PBM in query
    /// @return Returns true if all the tokenId are valid else false
    function areTokensValid(uint256[] memory tokenIds) external view returns (bool);

    /// @notice gets the amount of underlying ERC20 tokens each of the the PBM type holds
    /// @param tokenId The id for the PBM in query
    /// @return Returns the initial ERC20 balance of the PBM account
    function getTokenValue(uint256 tokenId) external view returns (uint256);

    /// @notice Retreive the details for a PBM
    /// @param tokenId The id for the PBM in query
    /// @return spotAmount Amount of underlying ERC20 held by the each of the PBM token
    /// @return expiry  Expiry time (in epoch) for the PBM type
    function getTokenDetails(uint256 tokenId) external view returns (uint256 spotAmount, uint256 expiry);

    /// @notice Creates a PBM token type, with all its necessary details
    /// @param spotAmount The number of ERC-20 tokens that is used as the underlying currency for PBM
    /// @param tokenExpiry The expiry date (in epoch) of th PBM type
    /// @param tokenURI the URI containting the metadata (opensea standard for ERC1155) for the  PBM type
    /// @param postExpiryURI the URI containting the metadata (opensea standard for ERC1155) for the expired PBM type
    /// @param contractExpiry the expiry time (in epoch) for the overall PBM contract
    function createTokenType(
        uint256 spotAmount,
        uint256 tokenExpiry,
        string memory tokenURI,
        string memory postExpiryURI,
        uint256 contractExpiry
    ) external;

    /// @notice Event emitted when a new PBM token type is created
    /// @param tokenId The account from which the tokens were sent, i.e. the balance decreased
    /// @param amount The amount of tokens that were transferred
    /// @param expiry The time (in epoch) when the PBM type will expire
    event NewPBMTypeCreated(uint256 tokenId, uint256 amount, uint256 expiry);
}
","keccak256":"0xd0c2d7e8183f1fe4a012a9c96e9a82f313c2eede0b0193ad3f8b0f45f90aaa08","license":"MIT"},"contracts/NoDelegateCall.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

/// @title Prevents delegatecall to a contract
/// @notice Base contract that provides a modifier for preventing delegatecall to methods in a child contract
abstract contract NoDelegateCall {
    /// @dev The original address of this contract
    address private immutable original;

    constructor() {
        // Immutables are computed in the init code of the contract, and then inlined into the deployed bytecode.
        // In other words, this variable won't change when it's checked at runtime.
        original = address(this);
    }

    /// @dev Private method is used instead of inlining into modifier because modifiers are copied into each method,
    ///     and the use of immutable means the address bytes are copied in every place the modifier is used.
    function checkNotDelegateCall() private view {
        require(address(this) == original, "Delegate calls restricted");
    }

    /// @notice Prevents delegatecall into the modified method
    modifier noDelegateCall() {
        checkNotDelegateCall();
        _;
    }
}
","keccak256":"0x11bd3eb59d1d1d8ed1098972ee376ac3d9846b8a21b896b25811cdc3b5d61a2e","license":"BUSL-1.1"},"contracts/PBM.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./ERC20Helper.sol";
import "./PBMTokenManager.sol";
import "./IPBM.sol";

contract PBM is ERC1155, Ownable, Pausable, ReentrancyGuard, IPBM {
    // undelrying ERC-20 tokens
    address public spotToken = address(0);
    // address of the token manager
    address public pbmTokenManager = address(0);

    // tracks contract initialisation
    bool internal initialised = false;
    // time of expiry ( epoch )
    uint256 public contractExpiry;

    constructor() ERC1155("") {
        pbmTokenManager = address(new PBMTokenManager());
    }

    function initialise(address _spotToken, uint256 _expiry) external override onlyOwner {
        require(!initialised, "PBM: Already initialised");
        require(Address.isContract(_spotToken), "Invalid spot token");
        spotToken = _spotToken;
        contractExpiry = _expiry;
        initialised = true;
    }

    enum OrderStatus {
        PENDING,
        REDEEMED,
        CANCELLED
    }

    struct Order {
        uint256 orderValue; // how much this order cost.
        string orderId;
        address customerWallet;
        address fundDisbursementAddress; // need this to check upon redemption
        OrderStatus status;
    }

    // user_address -> token_id -> UserBalance
    // token_id being the account number
    // user_address is the user’s identifier
    mapping(address => mapping(uint256 => UserBalance)) private userBalances;

    function getUserBalance(
        address user,
        uint256 tokenId
    ) public view whenNotPaused returns (UserBalance memory userBalance) {
        UserBalance memory userBal = userBalances[user][tokenId];
        return userBal;
    }

    // order id hash => order mapping
    mapping(bytes32 => Order) public orders;

    // whitelist mapping
    mapping(address => bool) public whitelist;

    modifier onlyWhitelisted() {
        require(whitelist[_msgSender()], "You are not authorized to call this function");
        _;
    }

    modifier orderExists(string memory orderId) {
        bytes32 orderIdHash = keccak256(abi.encodePacked(orderId));
        require(orders[orderIdHash].orderValue > 0, "Order with this ID does not exist");
        _;
    }

    function addToWhitelist(address account) external onlyOwner {
        whitelist[account] = true;
    }

    function removeFromWhitelist(address account) external onlyOwner {
        whitelist[account] = false;
    }

    function createOrder(
        address customerWalletAddr,
        uint256 tokenId,
        string memory orderId,
        uint256 orderValue,
        address fundDisbursementAddr
    ) external whenNotPaused onlyWhitelisted {
        require(customerWalletAddr != address(0), "Invalid customer address");
        require(orderValue > 0, "Invalid order value");
        require(fundDisbursementAddr != address(0), "Invalid fund disbursement address");

        bytes32 orderIdHash = keccak256(abi.encodePacked(orderId));
        // must protect this, this ensures cannot call createOrder multiple times
        require(orders[orderIdHash].orderValue == 0, "Order with this ID already exists");
        // move the user's currentBalance into the order list
        // create Orders with the order_id, and how much this order_id cost.
        // update UserBalance[user][token_id]; currentBalance
        UserBalance storage userBalance = userBalances[customerWalletAddr][tokenId];
        require(userBalance.availableBalance >= orderValue, "Insufficient available funds");

        orders[orderIdHash] = Order(orderValue, orderId, customerWalletAddr, fundDisbursementAddr, OrderStatus.PENDING);

        userBalance.availableBalance -= orderValue;

        emit OrderCreated(customerWalletAddr, orderId, orderValue, fundDisbursementAddr);
    }

    function cancelOrder(
        string memory orderId,
        uint256 tokenId
    ) external whenNotPaused onlyWhitelisted orderExists(orderId) {
        bytes32 orderIdHash = keccak256(abi.encodePacked(orderId));
        require(orders[orderIdHash].status == OrderStatus.PENDING, "This order is no longer pending");
        uint256 order_value = orders[orderIdHash].orderValue;

        UserBalance storage userBalance = userBalances[orders[orderIdHash].customerWallet][tokenId];
        require(
            userBalance.walletBalance >= (userBalance.availableBalance + order_value),
            "Avail balance can never be greater than walletBalance"
        );
        require(userBalance.walletBalance > 0, "Invalid user balance.");

        // increase the availableBalance.
        // essentially in pay and cancel pay, we only touch the availableBalance.
        // wallet balance is only edited in the event of p2p transfer, or successful redeem.
        orders[orderIdHash].status = OrderStatus.CANCELLED;
        userBalance.availableBalance += order_value;
        emit OrderCanceled(orderId);
    }

    // update order status
    // credit merchant wallet
    // TBD: do we need to check if fundDisbursementAddress is a merchant?
    function redeemOrder(
        string memory orderId,
        uint256 tokenId,
        address userWallet
    ) external whenNotPaused onlyWhitelisted orderExists(orderId) {
        bytes32 orderIdHash = keccak256(abi.encodePacked(orderId));
        // this check is very important, to prevent this function() from being called more than once.
        require(orders[orderIdHash].status == OrderStatus.PENDING, "This order is no longer pending");
        uint256 order_value = orders[orderIdHash].orderValue;
        UserBalance storage userBalance = userBalances[userWallet][tokenId];
        require(userBalance.walletBalance >= order_value, "Insufficient wallet balance");

        require(
            userBalance.walletBalance >= userBalance.availableBalance,
            "Something is wrong, availableBalance must be deducted first"
        );
        userBalance.walletBalance -= order_value;

        orders[orderIdHash].status = OrderStatus.REDEEMED;

        ERC20Helper.safeTransfer(spotToken, orders[orderIdHash].fundDisbursementAddress, order_value);

        emit OrderRedeemed(userWallet, orderId);
    }

    /**
     * @dev See {IPBM-createPBMTokenType}.
     *
     * Requirements:
     *
     * - caller must be owner
     * - contract must not be expired
     * - `tokenExpiry` must be less than contract expiry
     * - `amount` should not be 0
     */

    function createPBMTokenType(
        uint256 spotAmount,
        uint256 tokenExpiry,
        string memory tokenURI,
        string memory postExpiryURI
    ) external override onlyOwner {
        PBMTokenManager(pbmTokenManager).createTokenType(
            spotAmount,
            tokenExpiry,
            tokenURI,
            postExpiryURI,
            contractExpiry
        );
    }

    /**
     * @dev See {IPBM-mint}.
     *     
     * IMPT: Before minting, the caller should approve the contract address to spend ERC-20 tokens on behalf of the caller.
     *       This can be done by calling the `approve` or `increaseMinterAllowance` functions of the ERC-20 contract and specifying `_spender` to be the PBM contract address. 
             Ref : https://eips.ethereum.org/EIPS/eip-20

       WARNING: Any contracts that externally call these mint() and batchMint() functions should implement some sort of reentrancy guard procedure (such as OpenZeppelin's ReentrancyGuard).
     *
     * Requirements:
     *
     * - contract must not be paused
     * - tokens must not be expired
     * - `tokenId` should be a valid id that has already been created
     * - caller should have the necessary amount of the ERC-20 tokens required to mint
     * - caller should have approved the PBM contract to spend the ERC-20 tokens
     * - receiver should not be blacklisted
     */

    function mint(uint256 tokenId, uint256 amount, address userAddress) public override whenNotPaused onlyOwner {
        // do we need to check whether user address already holds the account/tokenId?
        // block mint to user alr holds the account/tokenId
        require(balanceOf(userAddress, tokenId) == 0, "Address already holds this account");
        require(userAddress != address(0), "Invalid user address");
        require(amount == 1, "Amount can only be 1");

        // get token value here checks if the tokenId is created alr
        uint256 spotAmount = PBMTokenManager(pbmTokenManager).getTokenValue(tokenId);

        UserBalance storage userBalance = userBalances[userAddress][tokenId];
        userBalance.walletBalance += spotAmount;
        userBalance.availableBalance += spotAmount;

        ERC20Helper.safeTransferFrom(spotToken, _msgSender(), address(this), spotAmount);
        emit FundsAdded(userAddress, spotAmount);

        _mint(userAddress, tokenId, amount, "");
    }

    // instead of minting multiple token ids to a single address
    // mintBatch here would mint one token id (account) to multiple user addresses
    function mintBatch(uint256 tokenId, uint256 amount, address[] memory userAddresses) public whenNotPaused onlyOwner {
        // get token value here checks if the tokenId is created alr
        uint256 spotAmount = PBMTokenManager(pbmTokenManager).getTokenValue(tokenId);
        uint256 usersCount = userAddresses.length;
        uint256 totalSpotAmount = spotAmount * usersCount;
        ERC20Helper.safeTransferFrom(spotToken, _msgSender(), address(this), totalSpotAmount);

        // Loop over each address and mint
        for (uint256 i = 0; i < usersCount; i++) {
            address userAddress = userAddresses[i];
            require(balanceOf(userAddress, tokenId) == 0, "Address already holds this account");
            require(userAddress != address(0), "Invalid user address");
            require(amount == 1, "Amount can only be 1 for each token");

            UserBalance storage userBalance = userBalances[userAddress][tokenId];
            userBalance.walletBalance += spotAmount;
            userBalance.availableBalance += spotAmount;
            _mint(userAddress, tokenId, amount, "");
            emit FundsAdded(userAddress, spotAmount);
        }
    }

    /**
     * @dev See {IPBM-safeTransferFrom}.
     *
     * IMPT: This function doesn't actually transfer the underlying ERC20 tokens. Instead, it only updates the user balances within this contract.
     *
     * Requirements:
     *
     * - contract must not be paused.
     * - `amount` should be exactly 1.
     * - sender (`from` address) should have a positive available balance.
     * - recipient (`to` address) should not be the zero address.
     * - caller must be either the token owner or approved to transfer the token.
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        uint256 amount,
        bytes memory data
    ) public override(ERC1155, IPBM) whenNotPaused nonReentrant {
        require(amount == 1, "Amount can only be 1");
        require(userBalances[from][tokenId].availableBalance > 0, "Invalid available balance");
        require(to != address(0), "Invalid recipient address");
        require(from == _msgSender() || isApprovedForAll(from, _msgSender()), "Caller is not token owner or approved");

        uint256 transferAmount = userBalances[from][tokenId].availableBalance;

        userBalances[from][tokenId].walletBalance -= transferAmount;
        userBalances[from][tokenId].availableBalance -= transferAmount;

        userBalances[to][tokenId].walletBalance += transferAmount;
        userBalances[to][tokenId].availableBalance += transferAmount;

        emit UserBalanceUpdated(
            from,
            tokenId,
            userBalances[from][tokenId].walletBalance,
            userBalances[from][tokenId].availableBalance
        );
        emit UserBalanceUpdated(
            to,
            tokenId,
            userBalances[to][tokenId].walletBalance,
            userBalances[to][tokenId].availableBalance
        );
    }

    /**
     * @dev See {IPBM-safeBatchTransferFrom}.
     *
     * IMPT: This function doesn't actually transfer the underlying ERC20 tokens. Instead, it only updates the user balances within this contract.
     *
     * Requirements:
     *
     * - contract must not be paused.
     * - `amounts` for each tokenId should be exactly 1.
     * - sender (`from` address) should have a positive available balance for each tokenId.
     * - recipient (`to` address) should not be the zero address.
     * - caller must be either the token owner or approved to transfer the tokens.
     */
    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory tokenIds,
        uint256[] memory amounts,
        bytes memory data
    ) public override(ERC1155, IPBM) whenNotPaused nonReentrant {
        require(to != address(0), "Invalid recipient address");
        require(tokenIds.length == amounts.length, "TokenIDs and amounts length mismatch");

        for (uint256 i = 0; i < tokenIds.length; i++) {
            uint256 tokenId = tokenIds[i];
            uint256 amount = amounts[i];

            require(amount == 1, "Amount for each tokenId can only be 1");
            require(userBalances[from][tokenId].availableBalance > 0, "Invalid available balance for tokenId");

            uint256 transferAmount = userBalances[from][tokenId].availableBalance;

            userBalances[from][tokenId].walletBalance -= transferAmount;
            userBalances[from][tokenId].availableBalance -= transferAmount;

            userBalances[to][tokenId].walletBalance += transferAmount;
            userBalances[to][tokenId].availableBalance += transferAmount;

            emit UserBalanceUpdated(
                from,
                tokenId,
                userBalances[from][tokenId].walletBalance,
                userBalances[from][tokenId].availableBalance
            );
            emit UserBalanceUpdated(
                to,
                tokenId,
                userBalances[to][tokenId].walletBalance,
                userBalances[to][tokenId].availableBalance
            );
        }
    }

    /**
     * @dev See {IPBM-revokePBM}.
     * Requirements:
     *
     * - caller must be the owner of the contract
     * - token must be expired
     */

    function revokePBM() external onlyOwner whenNotPaused {
        ERC20 erc20 = ERC20(spotToken);
        uint256 valueOfTokens = erc20.balanceOf(address(this));
        ERC20Helper.safeTransfer(address(erc20), owner(), valueOfTokens);

        emit PBMrevokeWithdraw(_msgSender(), spotToken, valueOfTokens);
    }

    /**
     * @dev See {IPBM-getTokenDetails}.
     *
     */
    function getTokenDetails(uint256 tokenId) external view override returns (uint256, uint256) {
        return PBMTokenManager(pbmTokenManager).getTokenDetails(tokenId);
    }

    /**
     * @dev See {IPBM-uri}.
     *
     */
    function uri(uint256 tokenId) public view override(ERC1155, IPBM) returns (string memory) {
        return PBMTokenManager(pbmTokenManager).uri(tokenId);
    }

    /**
     * @dev see {Pausable _pause}
     *
     * Requirements :
     * - caller should be owner
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev see {Pausable _unpause}
     *
     * Requirements :
     * - caller should be owner
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    event OrderCreated(address customer, string orderId, uint256 orderValue, address fundDisbursementAddress);
    event OrderRedeemed(address customer, string orderId);
    event OrderCanceled(string orderId);
    event FundsAdded(address customer, uint256 spotAmount);
    event UserBalanceUpdated(
        address indexed user,
        uint256 tokenId,
        uint256 newWalletBalance,
        uint256 newAvailableBalance
    );
}
","keccak256":"0x9ed69e48a322a986058e745a5378839407a791e8b0a46d13ac03e07703dc289d","license":"MIT"},"contracts/PBMTokenManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./IPBMTokenManager.sol";
import "./NoDelegateCall.sol";

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract PBMTokenManager is Ownable, IPBMTokenManager, NoDelegateCall {
    using Strings for uint256;

    // counter used to create new token types
    uint256 internal tokenTypeCount = 1;

    // structure representing all the details of a PBM type
    struct TokenConfig {
        uint256 amount;
        uint256 expiry;
        string uri;
        string postExpiryURI;
    }

    // mapping of token ids to token details
    mapping(uint256 => TokenConfig) internal tokenTypes;

    constructor() {}

    /**
     * @dev See {IPBMTokenManager-createPBMTokenType}.
     *
     * Requirements:
     *
     * - caller must be owner ( PBM contract )
     * - contract must not be expired
     * - token expiry must be less than contract expiry
     * - `amount` should not be 0
     */
    function createTokenType(
        uint256 spotAmount,
        uint256 tokenExpiry,
        string memory tokenURI,
        string memory postExpiryURI,
        uint256 contractExpiry
    ) external override onlyOwner noDelegateCall {
        require(tokenExpiry <= contractExpiry, "Invalid token expiry-1");
        require(tokenExpiry > block.timestamp, "Invalid token expiry-2");
        require(spotAmount != 0, "Spot amount is 0");

        tokenTypes[tokenTypeCount].amount = spotAmount;
        tokenTypes[tokenTypeCount].expiry = tokenExpiry;
        tokenTypes[tokenTypeCount].uri = tokenURI;
        tokenTypes[tokenTypeCount].postExpiryURI = postExpiryURI;

        emit NewPBMTypeCreated(tokenTypeCount, spotAmount, tokenExpiry);
        tokenTypeCount += 1;
    }

    /**
     * @dev See {IPBMTokenManager-getTokenValue}.
     *
     * Requirements:
     *
     * - `tokenId` should be a valid id that has already been created
     */
    function getTokenValue(uint256 tokenId) external view override returns (uint256) {
        require(
            tokenTypes[tokenId].amount != 0 && block.timestamp < tokenTypes[tokenId].expiry,
            "PBM: Invalid Token Id(s)"
        );
        return tokenTypes[tokenId].amount;
    }

    /**
     * @dev See {IPBMTokenManager-uri}.
     *
     */
    function uri(uint256 tokenId) external view override returns (string memory) {
        if (block.timestamp >= tokenTypes[tokenId].expiry) {
            return tokenTypes[tokenId].postExpiryURI;
        }
        return tokenTypes[tokenId].uri;
    }

    /**
     * @dev See {IPBMTokenManager-areTokensValid}.
     *
     */
    function areTokensValid(uint256[] memory tokenIds) external view override returns (bool) {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            if (block.timestamp > tokenTypes[tokenIds[i]].expiry || tokenTypes[tokenIds[i]].amount == 0) {
                return false;
            }
        }
        return true;
    }

    /**
     * @dev See {IPBMTokenManager-getTokenDetails}.
     *
     * Requirements:
     *
     * - `tokenId` should be a valid id that has already been created
     */
    function getTokenDetails(uint256 tokenId) external view override returns (uint256, uint256) {
        require(tokenTypes[tokenId].amount != 0, "PBM: Invalid Token Id(s)");
        return (tokenTypes[tokenId].amount, tokenTypes[tokenId].expiry);
    }
}
